import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "./about.css"
import homepageimage from './Images/homepageimage.webp';

export function About(){
    return(
        <>
            <div className="mainbodydiv">
                <div className="container-fluid row justify-content-around col-lg-12 m-5">
                    <div className="col-lg-5">
                        <h1>About ME : </h1>
                        <h6>Hi, I am Samuvel Ananth, Certified Full Stack Developer</h6>
                    </div>
                    <div className="col-lg-5">
                        <img src={homepageimage} width="100%"/>
                    </div>
                </div>
            </div>
        </>
    );
}
