import React from "react";
import htmlcertificate from './Images/htmlcertificate.png';
import csscertificate from './Images/csscertificate.png';
import javascriptcertificate from './Images/javascriptcertificate.png';
import metafrontend from './Images/metafrontend.png';
import mongodbcertificate from './Images/mongodbcertificate.png';
import metabackend from './Images/metabackend.png';
import './certificate.css';

export function Certificate(){
    return (
        <>
            <h1 className="text-center pt-3 pb-3" id="certificateScroll">Certificate</h1>
            <div className="container-fluid justify-content-around d-flex">
                <div className="col-lg-4 text-center">
                    <img src={htmlcertificate} alt="no images found" width="400" className="certimage"/><br/>
                    <label className="text-center certfont p-1">Full Stack Development (MERN) - HTML</label>
                </div>
                <div className="col-lg-4 text-center">
                    <img src={csscertificate} alt="no images found" width="400" className="certimage"/><br/>
                    <label className="text-center certfont p-1">Full Stack Development (MERN) - CSS</label>
                </div>
                <div className="col-lg-4 text-center">
                    <img src={javascriptcertificate} alt="no images found" width="400" className="certimage"/><br/>
                    <label className="text-center certfont p-1">Programming with JavaScrip - META</label>
                </div>
            </div>
            <div className="container-fluid justify-content-around d-flex p-5">
                <div className="col-lg-4 text-center">
                    <img src={metafrontend} alt="no images found" width="400" className="certimage"/><br/>
                    <label className="text-center certfont p-1">Front End Development - META</label>
                </div>
                <div className="col-lg-4 text-center">
                    <img src={mongodbcertificate} alt="no images found" width="400" className="certimage"/><br/>
                    <label className="text-center certfont p-1">CRUD Opertions - MongoDB</label>
                </div>
                <div className="col-lg-4 text-center">
                    <img src={metabackend} alt="no images found" width="400" className="certimage"/><br/>
                    <label className="text-center certfont p-1">Front End Development - META</label>
                </div>
            </div>
        </>
    );
}
