import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "./about.css"
import homepageimage from './Images/homepageimage.webp';

export function About(){
    return(
        <>
            <div className="mainbodydiv">
                <div className="container-fluid row justify-content-around col-lg-12">
                    <div className="col-lg-4 mt-5">
                        <h5 className="container ml-auto mr-auto justify-content-center">A Full-stack developer is proficient in both front-end and back-end development. Their duties include designing, creating and maintaining fully developed platforms with servers or databases. These servers do not require any additional third-party software to create a full network from the start. Full-stack developers will be required to manage a project from conception to final product, requiring good organisational skills and attention to details.</h5>
                    </div>
                    <div className="col-lg-6 mt-5">
                        <img src={homepageimage} width="650"/>
                    </div>
                </div>
            </div>
        </>
    );
}
