import React from "react";
import htmlcertificate from './Images/htmlcertificate.png';
import csscertificate from './Images/csscertificate.png';
import javascriptcertificate from './Images/javascriptcertificate.png';
import './certificate.css';

export function Certificate(){
    return (
        <>
            <h1 className="text-center pt-3 pb-3" id="certificateScroll">Certificate</h1>
            <div className="container-fluid justify-content-around d-flex">
                <div className="col-lg-4">
                    <img src={htmlcertificate} alt="no images found" width="420" className="certimage"/><br/>
                    <label className="text-center certfont">Full Stack Development (MERN) - HTML</label>
                </div>
                <div className="col-lg-4">
                    <img src={csscertificate} alt="no images found" width="420" className="certimage"/><br/>
                </div>
                <div className="col-lg-4">
                    <img src={javascriptcertificate} alt="no images found" width="420"/><br/>
                </div>
            </div>
        </>
    );
}
